import React, { useContext } from "react";
import { Box, Typography, Grid, Button } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { LanguageContext } from "../context/LanguageContext";

const translations = {
    BM: { title: "Anda seorang?", normal: "Normal", rural: "Luar Bandar", easy: "Mudah", senior: "Warga Emas" },
    BI: { title: "Are you a?", normal: "Normal", rural: "Rural Area", easy: "Easy", senior: "Senior Citizen" },
};

const UserMode: React.FC = () => {
    const navigate = useNavigate();
    const { language } = useContext(LanguageContext);

    const handleSelect = (mode: string) => {
        console.log("User selected mode:", mode);
        navigate("/signin");
    };

    return (
        <Box textAlign="center" mt={5}>
            <Typography variant="h4">{translations[language].title}</Typography>
            <Grid container spacing={2} justifyContent="center" mt={2}>
                <Grid item><Button variant="contained" onClick={() => handleSelect("normal")}>{translations[language].normal}</Button></Grid>
                <Grid item><Button variant="contained" onClick={() => handleSelect("rural")}>{translations[language].rural}</Button></Grid>
                <Grid item><Button variant="contained" onClick={() => handleSelect("easy")}>{translations[language].easy}</Button></Grid>
                <Grid item><Button variant="contained" onClick={() => handleSelect("senior")}>{translations[language].senior}</Button></Grid>
            </Grid>
        </Box>
    );
};

export default UserMode;
