import React, { useRef, useState } from 'react';
import Webcam from 'react-webcam';
import { Button, Container, Typography, Box, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';
// import { extractICData } from '../services/api'; // Commented out for Bypass

const SignUpStep1: React.FC = () => {
    const webcamRef = useRef<Webcam>(null);
    const [frontImg, setFrontImg] = useState<string | null>(null);
    const [backImg, setBackImg] = useState<string | null>(null);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const capture = (setImg: Function) => {
        const imageSrc = webcamRef.current?.getScreenshot();
        if (imageSrc) setImg(imageSrc);
    };

    // This function is kept but not used in Bypass Mode
    const dataURLtoFile = (dataurl: string, filename: string) => {
        let arr = dataurl.split(','), mime = arr[0].match(/:(.*?);/)![1];
        let bstr = atob(arr[1]), n = bstr.length, u8arr = new Uint8Array(n);
        while (n--) u8arr[n] = bstr.charCodeAt(n);
        return new File([u8arr], filename, { type: mime });
    }

    const handleNext = async () => {
        // Optional: Still require images to feel "real"
        if (!frontImg || !backImg) return alert("Please capture both sides (Mock Mode)");

        setLoading(true);

        // ---------------------------------------------------------
        // üöÄ FRONTEND BYPASS MODE
        // Instead of calling the API, we just pretend to wait 1 second
        // and then send dummy data to the next page.
        // ---------------------------------------------------------

        setTimeout(() => {
            console.log("‚ö†Ô∏è SKIPPING SERVER: Using Mock Data");

            // The data you wanted to use:
            const mockResult = {
                fullName: "TAN SENG HONG",
                icNumber: "990101-14-5678", // Added hyphens for standard format
                address: "N277 JALAN PERKASA 1 TAMAN MALURI, 55100, KUALA LUMPUR"
            };

            setLoading(false);

            // Navigate immediately with the mock data
            navigate('/signup-step2', {
                state: {
                    icNumber: mockResult.icNumber,
                    fullName: mockResult.fullName,
                    // address: mockResult.address // Pass this if Step 2 needs it
                }
            });

        }, 1000); // 1 second fake delay
    };

    return (
        <Container>
            <Typography variant="h4" sx={{ my: 2 }}>Scan MyKad (Bypass Mode)</Typography>

            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                {/* Webcam View */}
                {!frontImg || !backImg ? (
                    <Webcam
                        audio={false}
                        ref={webcamRef}
                        screenshotFormat="image/jpeg"
                        width="100%"
                    />
                ) : null}

                {/* Buttons */}
                {!frontImg && (
                    <Button variant="contained" onClick={() => capture(setFrontImg)}>Capture Front</Button>
                )}
                {frontImg && !backImg && (
                    <Button variant="contained" onClick={() => capture(setBackImg)}>Capture Back</Button>
                )}

                {frontImg && backImg && (
                    <Button variant="contained" color="success" onClick={handleNext} disabled={loading}>
                        {loading ? <CircularProgress size={24} /> : "Process & Next (Mock)"}
                    </Button>
                )}
            </Box>
        </Container>
    );
};

export default SignUpStep1;
