// src/pages/SignUp1.tsx
import React, { useState, useRef } from "react";
import Webcam from "react-webcam";
import { Box, Button, Typography } from "@mui/material";
import { useNavigate } from "react-router-dom";
import Tesseract from "tesseract.js";

const SignUp1: React.FC = () => {
    const [frontIC, setFrontIC] = useState<string | null>(null);
    const [backIC, setBackIC] = useState<string | null>(null);
    const [loading, setLoading] = useState(false);
    const webcamRef = useRef<Webcam>(null);
    const navigate = useNavigate();

    const captureIC = () => {
        const imageSrc = webcamRef.current?.getScreenshot();
        if (!imageSrc) return;

        if (!frontIC) setFrontIC(imageSrc);
        else if (!backIC) setBackIC(imageSrc);
    };

    const handleRetake = () => {
        if (backIC) setBackIC(null);
        else if (frontIC) setFrontIC(null);
    };

    const handleNext = async () => {
        if (!frontIC || !backIC) return alert("Please scan both front and back of your IC");

        try {
            setLoading(true);

            // OCR front and back IC
            const frontResult = await Tesseract.recognize(frontIC, "eng", { logger: (m) => console.log(m) });
            const backResult = await Tesseract.recognize(backIC, "eng", { logger: (m) => console.log(m) });

            const frontText = frontResult.data.text;
            const backText = backResult.data.text;

            // Simple parsing (adjust regex based on your IC format)
            const fullNameMatch = frontText.match(/Name[:\s]+([A-Za-z\s]+)/i);
            const icNumberMatch = frontText.match(/(\d{6}-\d{2}-\d{4})/);
            const addressMatch = backText.match(/Address[:\s]+([\w\s,]+)/i);

            const extractedData = {
                fullName: fullNameMatch ? fullNameMatch[1].trim() : "",
                icNumber: icNumberMatch ? icNumberMatch[0] : "",
                address: addressMatch ? addressMatch[1].trim() : "",
            };

            console.log("Extracted IC data:", extractedData);

            // Navigate to Step 2 with extracted data directly
            navigate("/signup-step2", { state: extractedData });
        } catch (err) {
            console.error(err);
            alert("Failed to extract IC info. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box textAlign="center" mt={5}>
            <Typography variant="h4" gutterBottom>
                Sign Up
            </Typography>

            {!frontIC || !backIC ? (
                <>
                    <Webcam
                        audio={false}
                        ref={webcamRef}
                        screenshotFormat="image/png"
                        width={300}
                    />
                    <Box mt={2}>
                        <Button variant="contained" onClick={captureIC}>
                            {!frontIC ? "Scan Front of IC" : "Scan Back of IC"}
                        </Button>
                    </Box>
                </>
            ) : (
                <>
                    <Box display="flex" justifyContent="center" gap={2} mt={2}>
                        <Box textAlign="center">
                            <Typography variant="subtitle1">Front of IC</Typography>
                            <img src={frontIC} alt="Front IC" width={150} />
                        </Box>
                        <Box textAlign="center">
                            <Typography variant="subtitle1">Back of IC</Typography>
                            <img src={backIC} alt="Back IC" width={150} />
                        </Box>
                    </Box>

                    <Box mt={3}>
                        <Button
                            variant="contained"
                            onClick={handleNext}
                            sx={{ mr: 2 }}
                            disabled={loading}
                        >
                            {loading ? "Processing..." : "Next"}
                        </Button>
                        <Button variant="outlined" onClick={handleRetake}>
                            Retake
                        </Button>
                    </Box>
                </>
            )}
        </Box>
    );
};

export default SignUp1;
