import React, { useState } from 'react';
import { Button, Container, Typography, Box, Card, CardContent } from '@mui/material';
import VoiceInput from '../components/VoiceInput'; // Use the component provided previously
import { initiateLogin, verifyLogin } from '../services/api';

const SignIn: React.FC = () => {
    const [step, setStep] = useState(1); // 1 = Enter IC, 2 = Answer Question
    const [icNumber, setIcNumber] = useState('');
    const [securityQuestion, setSecurityQuestion] = useState('');
    const [voiceAnswer, setVoiceAnswer] = useState('');
    const [loading, setLoading] = useState(false);

    // Step 1: Check IC and Get Question
    const handleCheckIC = async () => {
        if (!icNumber) return alert("Please enter IC Number");
        setLoading(true);

        const result = await initiateLogin(icNumber);
        setLoading(false);

        if (result.success) {
            setSecurityQuestion(result.question);
            setStep(2); // Move to next step
        } else {
            alert("User not found or connection error.");
        }
    };

    // Step 2: Verify Voice Answer
    const handleLogin = async () => {
        if (!voiceAnswer) return alert("Please speak or type your answer");
        setLoading(true);

        const result = await verifyLogin(icNumber, securityQuestion, voiceAnswer);
        setLoading(false);

        if (result.success) {
            alert(`LOGIN SUCCESSFUL! Welcome ${result.user.fullName}`);
        } else {
            alert("Login Failed: Incorrect Answer. Please try again.");
        }
    };

    return (
        <Container maxWidth="sm">
            <Box sx={{ mt: 8, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <Typography variant="h4" fontWeight="bold" gutterBottom>
                    MyKad Secure Login
                </Typography>

                <Card sx={{ width: '100%', mt: 3, boxShadow: 3 }}>
                    <CardContent sx={{ display: 'flex', flexDirection: 'column', gap: 3, p: 4 }}>

                        {/* --- STEP 1: ENTER IC --- */}
                        {step === 1 && (
                            <>
                                <Typography variant="body1" color="text.secondary">
                                    Please enter your IC number to begin.
                                </Typography>

                                <VoiceInput
                                    label="IC Number"
                                    value={icNumber}
                                    onChange={setIcNumber}
                                />

                                <Button
                                    variant="contained"
                                    size="large"
                                    onClick={handleCheckIC}
                                    disabled={loading}
                                >
                                    {loading ? "Checking..." : "Next"}
                                </Button>
                            </>
                        )}

                        {/* --- STEP 2: ANSWER QUESTION --- */}
                        {step === 2 && (
                            <>
                                <Box sx={{ bgcolor: '#e3f2fd', p: 2, borderRadius: 2 }}>
                                    <Typography variant="caption" color="primary" fontWeight="bold">
                                        SECURITY QUESTION
                                    </Typography>
                                    <Typography variant="h6">
                                        {securityQuestion}
                                    </Typography>
                                </Box>

                                <VoiceInput
                                    label="Your Answer (Voice Enabled)"
                                    value={voiceAnswer}
                                    onChange={setVoiceAnswer}
                                />

                                <Button
                                    variant="contained"
                                    size="large"
                                    color="success"
                                    onClick={handleLogin}
                                    disabled={loading}
                                >
                                    {loading ? "Verifying..." : "Login"}
                                </Button>

                                <Button onClick={() => setStep(1)} color="inherit">
                                    Back
                                </Button>
                            </>
                        )}

                    </CardContent>
                </Card>
            </Box>
        </Container>
    );
};

export default SignIn;
