import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Box, Typography, Button } from "@mui/material";
import { scanIC } from "../services/api";

const SignUpStep1: React.FC = () => {
    const navigate = useNavigate();
    const [frontFile, setFrontFile] = useState<File | null>(null);
    const [backFile, setBackFile] = useState<File | null>(null);

    const handleUpload = async () => {
        if (frontFile && backFile) {
            try {
                await scanIC(frontFile, backFile);
                navigate("/signup-step2");
            } catch (err) {
                console.error(err);
            }
        }
    };

    return (
        <Box textAlign="center" mt={5}>
            <Typography variant="h4">Welcome</Typography>
            <Typography>Create your account</Typography>

            <Box mt={3}>
                <Typography>Scan Front of IC</Typography>
                <input type="file" onChange={(e) => e.target.files && setFrontFile(e.target.files[0])} />
            </Box>
            <Box mt={3}>
                <Typography>Scan Back of IC</Typography>
                <input type="file" onChange={(e) => e.target.files && setBackFile(e.target.files[0])} />
            </Box>

            <Button variant="contained" sx={{ mt: 3 }} onClick={handleUpload}>
                Next
            </Button>
        </Box>
    );
};

export default SignUpStep1;
