import React, { useContext } from "react";
import { useNavigate } from "react-router-dom";
import { Button, Typography, Box } from "@mui/material";
import { LanguageContext } from "../context/LanguageContext";

const translations = {
    BM: { login: "Log Masuk", signup: "Daftar", logo: "Logo BM" },
    BO: { login: "Login", signup: "Sign Up", logo: "My App Logo" },
};

const Home: React.FC = () => {
    const navigate = useNavigate();
    const { language, setLanguage } = useContext(LanguageContext);

    return (
        <Box textAlign="center" mt={10}>
            <Typography variant="h3">{translations[language].logo}</Typography>

            <Button
                variant="outlined"
                onClick={() => setLanguage(language === "BM" ? "BO" : "BM")}
                sx={{ mt: 2 }}
            >
                Switch to {language === "BM" ? "BO" : "BM"}
            </Button>

            <Box mt={5}>
                <Button
                    variant="contained"
                    color="primary"
                    sx={{ mr: 2 }}
                    onClick={() => navigate("/signin")}
                >
                    {translations[language].login}
                </Button>
                <Button
                    variant="contained"
                    color="secondary"
                    onClick={() => navigate("/signup-step1")}
                >
                    {translations[language].signup}
                </Button>
            </Box>
        </Box>
    );
};

export default Home;
