import React, { useContext } from "react";
import { Box, Button, Typography } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { LanguageContext } from "../context/LanguageContext";

const Home: React.FC = () => {
    const navigate = useNavigate();
    const { language, toggleLanguage } = useContext(LanguageContext);

    return (
        <Box textAlign="center" mt={10}>
            <Typography variant="h3">My App Logo</Typography>

            {/* Single toggle language button */}
            <Box mt={3}>
                <Button variant="outlined" onClick={toggleLanguage}>
                    {language === "BM" ? "Switch to English" : "Tukar ke BM"}
                </Button>
            </Box>

            {/* Signup / Signin buttons */}
            <Box mt={5} display="flex" justifyContent="center" gap={2} flexWrap="wrap">
                <Button variant="contained" onClick={() => navigate("/signup")}>
                    {language === "BM" ? "Daftar" : "Sign Up"}
                </Button>
                <Button variant="contained" onClick={() => navigate("/signin")}>
                    {language === "BM" ? "Log Masuk" : "Sign In"}
                </Button>
            </Box>
        </Box>
    );
};

export default Home;
