// src/pages/SignUp1.tsx
import React, { useState, useRef } from "react";
import Webcam from "react-webcam";
import { Box, Button, Typography } from "@mui/material";

const SignUp1: React.FC = () => {
    const [capturedIC, setCapturedIC] = useState<string | null>(null);
    const webcamRef = useRef<Webcam>(null);

    const captureIC = () => {
        const imageSrc = webcamRef.current?.getScreenshot();
        if (imageSrc) setCapturedIC(imageSrc);
    };

    const handleSubmit = async () => {
        if (!capturedIC) return alert("Please scan your IC first");

        try {
            // Create a FormData object to send to backend
            const formData = new FormData();
            const blob = await (await fetch(capturedIC)).blob(); // convert base64 to blob
            formData.append("ic_image", blob, "ic_scan.png");

            // Example POST request
            // await axios.post("http://localhost:8000/signup", formData);

            alert("IC scanned and account created successfully!");
        } catch (err) {
            console.error(err);
            alert("Failed to submit IC. Please try again.");
        }
    };

    return (
        <Box textAlign="center" mt={5}>
            <Typography variant="h4" gutterBottom>
                Sign Up
            </Typography>

            {!capturedIC ? (
                <>
                    <Webcam
                        audio={false}
                        ref={webcamRef}
                        screenshotFormat="image/png"
                        width={300}
                    />
                    <Box mt={2}>
                        <Button variant="contained" onClick={captureIC}>
                            Scan IC
                        </Button>
                    </Box>
                </>
            ) : (
                <>
                    <img src={capturedIC} alt="Scanned IC" width={300} style={{ marginBottom: 10 }} />
                    <Box>
                        <Button variant="contained" onClick={handleSubmit} sx={{ mr: 2 }}>
                            Submit
                        </Button>
                        <Button variant="outlined" onClick={() => setCapturedIC(null)}>
                            Retake
                        </Button>
                    </Box>
                </>
            )}
        </Box>
    );
};

export default SignUp1;
