import React, { useState, useContext } from "react";
import { Box, Typography, TextField, Button } from "@mui/material";
import { LanguageContext } from "../context/LanguageContext";

const translations = {
    BM: { login: "Log Masuk", icNumber: "Nombor IC", uploadVoice: "Muat Naik Suara" },
    BI: { login: "Login", icNumber: "IC Number", uploadVoice: "Upload Voice" },
};

const SignIn: React.FC = () => {
    const { language } = useContext(LanguageContext);
    const [icNumber, setIcNumber] = useState("");
    const [audioFile, setAudioFile] = useState<File | null>(null);

    const handleLogin = () => {
        alert("Login attempt (backend not implemented yet)");
    };

    return (
        <Box textAlign="center" mt={5}>
            <Typography variant="h4">{translations[language].login}</Typography>
            <TextField label={translations[language].icNumber} value={icNumber} onChange={(e) => setIcNumber(e.target.value)} fullWidth sx={{ my: 1 }} />
            <Typography>{translations[language].uploadVoice}</Typography>
            <input type="file" accept="audio/*" onChange={(e) => e.target.files && setAudioFile(e.target.files[0])} />
            <Button variant="contained" sx={{ mt: 3 }} onClick={handleLogin}>Login</Button>
        </Box>
    );
};

export default SignIn;
