// src/pages/SignUp1.tsx
import React, { useState, useRef, useEffect } from "react";
import Webcam from "react-webcam";
import { Box, Button, Typography } from "@mui/material";
import { useNavigate } from "react-router-dom";

const SignUp1: React.FC = () => {
    const [frontIC, setFrontIC] = useState<string | null>(null);
    const [backIC, setBackIC] = useState<string | null>(null);
    const [loading, setLoading] = useState(false);

    const webcamRef = useRef<Webcam>(null);
    const navigate = useNavigate();

    // REAL PHONE CAMERA - back camera
    const videoConstraints = {
        width: { ideal: 1920 },
        height: { ideal: 1080 },
        facingMode: { exact: "environment" }, // Force BACK CAMERA
    };

    // COMPRESS IMAGE BEFORE SENDING TO BACKEND
    const compressImage = (base64: string, maxWidth = 1280, quality = 0.8): Promise<string> => {
        return new Promise((resolve) => {
            const img = new Image();
            img.src = base64;

            img.onload = () => {
                const canvas = document.createElement("canvas");
                const scale = maxWidth / img.width;

                canvas.width = maxWidth;
                canvas.height = img.height * scale;

                const ctx = canvas.getContext("2d");
                if (!ctx) return resolve(base64);

                ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
                resolve(canvas.toDataURL("image/jpeg", quality));
            };
        });
    };

    // CAPTURE IMAGE WITH COMPRESSION
    const captureIC = async () => {
        const imageSrc = webcamRef.current?.getScreenshot();
        if (!imageSrc) return alert("Unable to capture image, please retry.");

        const compressed = await compressImage(imageSrc);

        if (!frontIC) {
            setFrontIC(compressed);
            sessionStorage.setItem("ic_front", compressed);
        } else if (!backIC) {
            setBackIC(compressed);
            sessionStorage.setItem("ic_back", compressed);
        }
    };

    // SEND BOTH FRONT + BACK TO BACKEND OCR
    const handleNext = async () => {
        if (!frontIC || !backIC) return alert("Please scan both sides.");

        try {
            setLoading(true);

            const response = await fetch("http://localhost:8000/api/ocr/extract", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ front: frontIC, back: backIC }),
            });

            const result = await response.json();
            console.log("OCR RESULT:", result);

            if (!result.success) {
                alert("OCR failed — Please retake clearer photos.");
                return;
            }

            const extracted = {
                fullName: result.fullName || "",
                icNumber: result.icNumber || "",
                address: result.address || "",
            };

            sessionStorage.setItem("signupStep2", JSON.stringify(extracted));
            navigate("/signup-step2", { state: extracted });

        } catch (err) {
            console.error(err);
            alert("Error contacting OCR server.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box textAlign="center" mt={3}>
            <Typography variant="h4">Scan Your IC</Typography>

            {/* NO IMAGE TAKEN YET - SHOW CAMERA */}
            {!frontIC || !backIC ? (
                <>
                    <Webcam
                        audio={false}
                        ref={webcamRef}
                        screenshotFormat="image/jpeg"
                        width="100%"
                        videoConstraints={videoConstraints}
                        style={{
                            borderRadius: "12px",
                            width: "95%",
                            height: "auto",
                            marginTop: 10,
                        }}
                    />

                    <Typography color="gray" mt={1}>
                        • Use BACK camera for best clarity <br />
                        • Ensure IC fills the frame <br />
                        • Keep camera steady
                    </Typography>

                    <Button
                        variant="contained"
                        onClick={captureIC}
                        sx={{ mt: 2 }}
                    >
                        {!frontIC ? "Capture FRONT of IC" : "Capture BACK of IC"}
                    </Button>
                </>
            ) : (
                <>
                    {/* SHOW CAPTURED IMAGES + RETAKE BUTTONS */}
                    <Box display="flex" justifyContent="center" gap={4} mt={2}>
                        <Box>
                            <Typography>Front IC</Typography>
                            <img src={frontIC} width={180} style={{ borderRadius: 8 }} />
                            <Button
                                variant="outlined"
                                sx={{ mt: 1 }}
                                onClick={() => {
                                    setFrontIC(null);
                                    sessionStorage.removeItem("ic_front");
                                }}
                            >
                                Retake
                            </Button>
                        </Box>

                        <Box>
                            <Typography>Back IC</Typography>
                            <img src={backIC} width={180} style={{ borderRadius: 8 }} />
                            <Button
                                variant="outlined"
                                sx={{ mt: 1 }}
                                onClick={() => {
                                    setBackIC(null);
                                    sessionStorage.removeItem("ic_back");
                                }}
                            >
                                Retake
                            </Button>
                        </Box>
                    </Box>

                    <Button
                        variant="contained"
                        sx={{ mt: 3 }}
                        onClick={handleNext}
                        disabled={loading}
                    >
                        {loading ? "Extracting..." : "Next"}
                    </Button>
                </>
            )}
        </Box>
    );
};

export default SignUp1;
