import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const SignIn: React.FC = () => {
    const [icNumber, setIcNumber] = useState("");
    const navigate = useNavigate();

    const handleLogin = async () => {
        try {
            const response = await axios.post("http://localhost:8000/login", { ic_number: icNumber });
            console.log(response.data);
            // User exists, proceed to next step
            navigate("/voice-auth"); // or wherever next
        } catch (error: any) {
            if (error.response && error.response.status === 404) {
                alert("User not found! Redirecting to signup...");
                navigate("/signup-step1"); // redirect to signup
            } else {
                console.error(error);
                alert("Login failed. Please try again.");
            }
        }
    };

    return (
        <div>
            <h2>Sign In</h2>
            <input
                type="text"
                placeholder="IC Number"
                value={icNumber}
                onChange={(e) => setIcNumber(e.target.value)}
            />
            <button onClick={handleLogin}>Login</button>
        </div>
    );
};

export default SignIn;
