import React, { useRef, useState } from "react";
import Webcam from "react-webcam";
import { Box, Button, Typography } from "@mui/material";
import { useNavigate } from "react-router-dom";

const SignUp1: React.FC = () => {
    const webcamRef = useRef<Webcam>(null);
    const navigate = useNavigate();
    const [frontIC, setFrontIC] = useState<string | null>(null);
    const [backIC, setBackIC] = useState<string | null>(null);

    const capture = () => {
        const img = webcamRef.current?.getScreenshot();
        if (!img) return;

        if (!frontIC) setFrontIC(img);
        else if (!backIC) setBackIC(img);
    };

    const retake = () => {
        if (backIC) setBackIC(null);
        else if (frontIC) setFrontIC(null);
    };

    const next = () => {
        if (!frontIC || !backIC)
            return alert("Please capture both front and back of IC");

        // Store images in sessionStorage
        sessionStorage.setItem(
            "signupStep2",
            JSON.stringify({ frontIC, backIC })
        );

        navigate("/signup-step2");
    };

    return (
        <Box textAlign="center" mt={5}>
            <Typography variant="h4" gutterBottom>
                Scan Your IC
            </Typography>

            {!frontIC || !backIC ? (
                <>
                    <Webcam
                        ref={webcamRef}
                        screenshotFormat="image/png"
                        width={300}
                        videoConstraints={{ facingMode: "environment" }}
                    />
                    <Box mt={2}>
                        <Button variant="contained" onClick={capture}>
                            {!frontIC ? "Scan Front of IC" : "Scan Back of IC"}
                        </Button>
                    </Box>
                </>
            ) : (
                <>
                    <Box display="flex" justifyContent="center" gap={2} mt={2}>
                        <img src={frontIC} alt="Front IC" width={150} />
                        <img src={backIC} alt="Back IC" width={150} />
                    </Box>
                    <Box mt={3}>
                        <Button variant="outlined" onClick={retake} sx={{ mr: 2 }}>
                            Retake
                        </Button>
                        <Button variant="contained" onClick={next}>
                            Next
                        </Button>
                    </Box>
                </>
            )}
        </Box>
    );
};

export default SignUp1;
