import React, { useState, useContext } from "react";
import { Box, Typography, TextField, Button } from "@mui/material";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { LanguageContext } from "../context/LanguageContext";

const translations = {
    BM: {
        ic: "No. IC",
        name: "Nama",
        address: "Alamat",
        recordVoice: "Rakam Suara Anda",
        next: "Seterusnya",
        back: "Kembali",
        securityQ: "Soalan Keselamatan",
        question: "Soalan",
        answer: "Jawapan",
        modeTitle: "Anda seorang?",
        normal: "Normal",
        rural: "Luar Bandar",
        easy: "Mudah",
        senior: "Warga Emas",
    },
    BI: {
        ic: "IC Number",
        name: "Name",
        address: "Address",
        recordVoice: "Record Your Voice",
        next: "Next",
        back: "Back",
        securityQ: "Security Questions",
        question: "Question",
        answer: "Answer",
        modeTitle: "Are you a?",
        normal: "Normal",
        rural: "Rural Area",
        easy: "Easy",
        senior: "Senior Citizen",
    },
};

const SignUpWizard: React.FC = () => {
    const navigate = useNavigate();
    const { language } = useContext(LanguageContext);

    const [step, setStep] = useState(1);

    // Step 1: IC info
    const [icNumber, setIcNumber] = useState("");
    const [name, setName] = useState("");
    const [address, setAddress] = useState("");

    // Step 2: Voice
    const [voiceText, setVoiceText] = useState("");
    const [recording, setRecording] = useState(false);

    // Step 3: Security
    const [q1, setQ1] = useState("");
    const [a1, setA1] = useState("");
    const [q2, setQ2] = useState("");
    const [a2, setA2] = useState("");

    // Step 4: User mode
    const [mode, setMode] = useState("");

    const handleStartRecording = () => {
        const SpeechRecognition = (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
        if (!SpeechRecognition) return alert("Your browser does not support voice recording.");
        const recognition = new SpeechRecognition();
        recognition.lang = language === "BM" ? "ms-MY" : "en-US";
        recognition.interimResults = false;
        recognition.maxAlternatives = 1;

        recognition.onstart = () => setRecording(true);
        recognition.onresult = (event: any) => {
            const transcript = event.results[0][0].transcript;
            setVoiceText(transcript);
            setRecording(false);
        };
        recognition.onerror = (event: any) => {
            console.error(event.error);
            setRecording(false);
        };
        recognition.start();
    };

    const handleNext = async () => {
        try {
            if (step === 1) {
                if (!icNumber || !name || !address) return alert("Please fill all fields");
                await axios.post("http://localhost:8000/signup-step1", { ic_number: icNumber, name, address });
            }
            if (step === 2) {
                if (!voiceText) return alert(language === "BM" ? "Sila rakam suara anda" : "Please record your voice");
                await axios.post("http://localhost:8000/signup-voice", { ic_number: icNumber, voice_text: voiceText });
            }
            if (step === 3) {
                if (!q1 || !a1 || !q2 || !a2) return alert("Please fill all security questions");
                await axios.post("http://localhost:8000/signup-security", {
                    ic_number: icNumber,
                    question1: q1,
                    answer1: a1,
                    question2: q2,
                    answer2: a2,
                });
            }
            if (step === 4) {
                if (!mode) return alert("Please select a mode");
                await axios.post("http://localhost:8000/user-mode", { ic_number: icNumber, mode });
                alert(language === "BM" ? "Daftar berjaya!" : "Signup successful!");
                navigate("/signin");
            } else {
                setStep(step + 1);
            }
        } catch (err) {
            console.error(err);
            alert("Error occurred. Try again.");
        }
    };

    const handleBack = () => setStep(step - 1);

    return (
        <Box textAlign="center" mt={5}>
            {step === 1 && (
                <>
                    <Typography variant="h4">{language === "BM" ? "Buat Akaun" : "Create Your Account"}</Typography>
                    <Box mt={3}>
                        <TextField label={translations[language].ic} value={icNumber} onChange={(e) => setIcNumber(e.target.value)} sx={{ mb: 2 }} fullWidth />
                        <TextField label={translations[language].name} value={name} onChange={(e) => setName(e.target.value)} sx={{ mb: 2 }} fullWidth />
                        <TextField label={translations[language].address} value={address} onChange={(e) => setAddress(e.target.value)} sx={{ mb: 2 }} fullWidth />
                    </Box>
                </>
            )}

            {step === 2 && (
                <>
                    <Typography variant="h4">{translations[language].recordVoice}</Typography>
                    <Box mt={3}>
                        <Button variant="contained" onClick={handleStartRecording} disabled={recording}>
                            {recording ? "Recording..." : translations[language].recordVoice}
                        </Button>
                    </Box>
                    <p>Recorded Text: {voiceText}</p>
                </>
            )}

            {step === 3 && (
                <>
                    <Typography variant="h4">{translations[language].securityQ}</Typography>
                    <Box mt={3}>
                        <TextField label={`${translations[language].question} 1`} value={q1} onChange={(e) => setQ1(e.target.value)} sx={{ mb: 2 }} fullWidth />
                        <TextField label={`${translations[language].answer} 1`} value={a1} onChange={(e) => setA1(e.target.value)} sx={{ mb: 2 }} fullWidth />
                        <TextField label={`${translations[language].question} 2`} value={q2} onChange={(e) => setQ2(e.target.value)} sx={{ mb: 2 }} fullWidth />
                        <TextField label={`${translations[language].answer} 2`} value={a2} onChange={(e) => setA2(e.target.value)} sx={{ mb: 2 }} fullWidth />
                    </Box>
                </>
            )}

            {step === 4 && (
                <>
                    <Typography variant="h4">{translations[language].modeTitle}</Typography>
                    <Box mt={3} display="flex" justifyContent="center" gap={2} flexWrap="wrap">
                        {["normal", "rural", "easy", "senior"].map((m) => (
                            <Button key={m} variant={mode === m ? "contained" : "outlined"} onClick={() => setMode(m)}>
                                {translations[language][m as keyof typeof translations.BM]}
                            </Button>
                        ))}
                    </Box>
                </>
            )}

            <Box mt={3} display="flex" justifyContent="center" gap={2}>
                {step > 1 && <Button variant="outlined" onClick={handleBack}>{translations[language].back}</Button>}
                <Button variant="contained" onClick={handleNext}>{translations[language].next}</Button>
            </Box>
        </Box>
    );
};

export default SignUpWizard;
