import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { Box, Typography, Button } from "@mui/material";
import { scanIC } from "../services/api";
import { LanguageContext } from "../context/LanguageContext";

const translations = {
    BM: { welcome: "Selamat Datang", createAccount: "Cipta akaun anda", scanFront: "Imbas Bahagian Depan IC", scanBack: "Imbas Bahagian Belakang IC", next: "Seterusnya" },
    BO: { welcome: "Welcome", createAccount: "Create your account", scanFront: "Scan Front of IC", scanBack: "Scan Back of IC", next: "Next" },
};

const SignUpStep1: React.FC = () => {
    const navigate = useNavigate();
    const { language } = useContext(LanguageContext);

    const [frontFile, setFrontFile] = useState<File | null>(null);
    const [backFile, setBackFile] = useState<File | null>(null);

    const handleUpload = async () => {
        if (frontFile && backFile) {
            try {
                await scanIC(frontFile, backFile);
                navigate("/signup-step2");
            } catch (err) {
                console.error(err);
            }
        }
    };

    return (
        <Box textAlign="center" mt={5}>
            <Typography variant="h4">{translations[language].welcome}</Typography>
            <Typography>{translations[language].createAccount}</Typography>

            <Box mt={3}>
                <Typography>{translations[language].scanFront}</Typography>
                <input type="file" onChange={(e) => e.target.files && setFrontFile(e.target.files[0])} />
            </Box>
            <Box mt={3}>
                <Typography>{translations[language].scanBack}</Typography>
                <input type="file" onChange={(e) => e.target.files && setBackFile(e.target.files[0])} />
            </Box>

            <Button variant="contained" sx={{ mt: 3 }} onClick={handleUpload}>
                {translations[language].next}
            </Button>
        </Box>
    );
};

export default SignUpStep1;
