import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { Box, Typography, Button } from "@mui/material";
import { voiceEmbed } from "../services/api";
import { LanguageContext } from "../context/LanguageContext";

const translations = {
    BM: { recordVoice: "Rakam Suara Anda", next: "Seterusnya" },
    BO: { recordVoice: "Record Your Voice", next: "Next" },
};

const SignUpVoice: React.FC = () => {
    const navigate = useNavigate();
    const { language } = useContext(LanguageContext);

    const [audioFile, setAudioFile] = useState<File | null>(null);

    const handleUpload = async () => {
        if (audioFile) {
            try {
                await voiceEmbed(audioFile);
                navigate("/signup-security");
            } catch (err) {
                console.error(err);
            }
        }
    };

    return (
        <Box textAlign="center" mt={5}>
            <Typography variant="h4">{translations[language].recordVoice}</Typography>
            <input type="file" accept="audio/*" onChange={(e) => e.target.files && setAudioFile(e.target.files[0])} />
            <Box mt={3}>
                <Button variant="contained" onClick={handleUpload}>
                    {translations[language].next}
                </Button>
            </Box>
        </Box>
    );
};

export default SignUpVoice;
