import React, { useState, useContext } from "react";
import { Box, Typography, Button, TextField } from "@mui/material";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { LanguageContext } from "../context/LanguageContext";

const SignUpStep1: React.FC = () => {
    const navigate = useNavigate();
    const { language } = useContext(LanguageContext);

    const [frontIC, setFrontIC] = useState<File | null>(null);
    const [backIC, setBackIC] = useState<File | null>(null);
    const [icNumber, setIcNumber] = useState("");
    const [name, setName] = useState("");
    const [address, setAddress] = useState("");

    // Simulate extracting info from IC (replace with real OCR)
    const handleExtractInfo = () => {
        if (!frontIC || !backIC) return alert("Please upload both front and back IC images");
        // For demo, just fill with dummy data
        setIcNumber("1234567890");
        setName("John Doe");
        setAddress("123 Main Street");
    };

    const handleNext = async () => {
        if (!icNumber || !name || !address) return alert("Please fill all fields");
        try {
            await axios.post("http://localhost:8000/signup-step1", { ic_number: icNumber, name, address });
            navigate("/signup-voice", { state: { icNumber } });
        } catch (err: any) {
            alert(err.response?.data?.detail || "Failed to save info");
        }
    };

    return (
        <Box textAlign="center" mt={5}>
            <Typography variant="h4">{language === "BM" ? "Imbas IC Anda" : "Scan Your IC"}</Typography>

            <Box mt={3}>
                <input type="file" accept="image/*" onChange={(e) => e.target.files && setFrontIC(e.target.files[0])} />
                <Typography>{language === "BM" ? "Front of IC" : "Front of IC"}</Typography>

                <input type="file" accept="image/*" onChange={(e) => e.target.files && setBackIC(e.target.files[0])} />
                <Typography>{language === "BM" ? "Back of IC" : "Back of IC"}</Typography>

                <Button variant="contained" onClick={handleExtractInfo} sx={{ mt: 2 }}>
                    {language === "BM" ? "Dapatkan Maklumat" : "Extract Info"}
                </Button>
            </Box>

            {icNumber && (
                <Box mt={3}>
                    <TextField label="IC Number" value={icNumber} onChange={(e) => setIcNumber(e.target.value)} sx={{ mb: 2 }} />
                    <TextField label={language === "BM" ? "Nama" : "Name"} value={name} onChange={(e) => setName(e.target.value)} sx={{ mb: 2 }} />
                    <TextField label={language === "BM" ? "Alamat" : "Address"} value={address} onChange={(e) => setAddress(e.target.value)} sx={{ mb: 2 }} />
                </Box>
            )}

            {icNumber && (
                <Box mt={2}>
                    <Button variant="contained" onClick={handleNext}>
                        {language === "BM" ? "Seterusnya" : "Next"}
                    </Button>
                </Box>
            )}
        </Box>
    );
};

export default SignUpStep1;
