import React, { useState, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Container, Typography, Box, Button, Fab, CircularProgress, Card } from '@mui/material';
import MicIcon from '@mui/icons-material/Mic';
import StopIcon from '@mui/icons-material/Stop';
import { keyframes } from '@emotion/react';
import axios from 'axios';

// Pulse animation for the big mic
const pulse = keyframes`
  0% { box-shadow: 0 0 0 0 rgba(25, 118, 210, 0.4); transform: scale(1); }
  50% { box-shadow: 0 0 0 20px rgba(25, 118, 210, 0); transform: scale(1.1); }
  100% { box-shadow: 0 0 0 0 rgba(25, 118, 210, 0); transform: scale(1); }
`;

const SignUpStep2: React.FC = () => {
    const { state } = useLocation(); // Get data passed from Step 1
    const navigate = useNavigate();

    // Default fallback if state is missing (for testing)
    const fullName = state?.fullName || "User";
    const icNumber = state?.icNumber || "000000-00-0000";

    const [isRecording, setIsRecording] = useState(false);
    const [audioBlob, setAudioBlob] = useState<Blob | null>(null);
    const [uploading, setUploading] = useState(false);
    const mediaRecorderRef = useRef<MediaRecorder | null>(null);

    const startRecording = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            const mediaRecorder = new MediaRecorder(stream);
            mediaRecorderRef.current = mediaRecorder;
            const chunks: BlobPart[] = [];

            mediaRecorder.ondataavailable = (e) => chunks.push(e.data);
            mediaRecorder.onstop = () => {
                const blob = new Blob(chunks, { type: 'audio/wav' });
                setAudioBlob(blob);
            };

            mediaRecorder.start();
            setIsRecording(true);
        } catch (err) {
            alert("Microphone access denied or not found.");
        }
    };

    const stopRecording = () => {
        mediaRecorderRef.current?.stop();
        setIsRecording(false);
    };

    const handleSaveAndNext = async () => {
        if (!audioBlob) return alert("Please record your voice first.");

        setUploading(true);
        const formData = new FormData();
        formData.append("voice_file", audioBlob, "voice.wav");
        formData.append("icNumber", icNumber);

        try {
            // Send to backend
            await axios.post("http://localhost:8000/signup-voice", formData);
            alert("Voice saved successfully!");
            // Navigate to next step (e.g., Security Questions or Login)
            navigate('/signin');
        } catch (error) {
            console.error(error);
            alert("Failed to save voice to server.");
        } finally {
            setUploading(false);
        }
    };

    return (
        <Container maxWidth="sm" sx={{ mt: 5, textAlign: 'center' }}>
            <Typography variant="h4" gutterBottom>Voice Registration</Typography>

            <Card sx={{ p: 4, mb: 4, backgroundColor: '#f5f5f5' }}>
                <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                    Please read the following sentence aloud:
                </Typography>

                <Typography variant="h5" fontWeight="bold" color="primary" sx={{ my: 3, fontStyle: 'italic' }}>
                    "My name is {fullName}, and I verify that my IC number is {icNumber}."
                </Typography>
            </Card>

            <Box sx={{ height: 150, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                {/* BIG CENTERED MIC BUTTON */}
                <Fab
                    color={isRecording ? "error" : "primary"}
                    aria-label="record"
                    onClick={isRecording ? stopRecording : startRecording}
                    sx={{
                        width: 100,
                        height: 100,
                        fontSize: '3rem',
                        animation: isRecording ? `${pulse} 1.5s infinite` : 'none'
                    }}
                >
                    {isRecording ? <StopIcon sx={{ fontSize: 50 }} /> : <MicIcon sx={{ fontSize: 50 }} />}
                </Fab>
            </Box>

            <Typography variant="caption" display="block" sx={{ mt: 2, mb: 4 }}>
                {isRecording ? "Recording... Tap to Stop" : audioBlob ? "Recording Saved. Ready to Submit." : "Tap Microphone to Start"}
            </Typography>

            <Button
                variant="contained"
                size="large"
                fullWidth
                disabled={!audioBlob || uploading}
                onClick={handleSaveAndNext}
            >
                {uploading ? <CircularProgress size={24} /> : "Save Voice & Finish"}
            </Button>
        </Container>
    );
};

export default SignUpStep2;
