import React, { useRef, useState } from 'react';
import Webcam from 'react-webcam';
import { Button, Container, Typography, Box, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { extractICData } from '../services/api';

const SignUpStep1: React.FC = () => {
    const webcamRef = useRef<Webcam>(null);
    const [frontImg, setFrontImg] = useState<string | null>(null);
    const [backImg, setBackImg] = useState<string | null>(null);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const capture = (setImg: Function) => {
        const imageSrc = webcamRef.current?.getScreenshot();
        if (imageSrc) setImg(imageSrc);
    };

    const dataURLtoFile = (dataurl: string, filename: string) => {
        let arr = dataurl.split(','), mime = arr[0].match(/:(.*?);/)![1];
        let bstr = atob(arr[1]), n = bstr.length, u8arr = new Uint8Array(n);
        while (n--) u8arr[n] = bstr.charCodeAt(n);
        return new File([u8arr], filename, { type: mime });
    }

    const handleNext = async () => {
        if (!frontImg || !backImg) return alert("Please capture both sides");

        setLoading(true);
        try {
            const frontFile = dataURLtoFile(frontImg, "front.jpg");
            const backFile = dataURLtoFile(backImg, "back.jpg");

            const result = await extractICData(frontFile, backFile);

            if (result.success) {
                // ✅ SUCCESS: Navigate to Step 2
                // Ensure your App.tsx has a route path="/signup-step2" or "/signup/step2" matching this:
                navigate('/signup-step2', {
                    state: {
                        icNumber: result.icNumber,
                        fullName: result.fullName
                    }
                });
            } else {
                alert("OCR Failed. Please Retake.");
                setFrontImg(null); // Reset to force retake
            }
        } catch (err: any) {
            console.error(err);
            // ✅ IMPROVED ERROR HANDLING
            if (err.response && err.response.data && err.response.data.error) {
                alert(`Error: ${err.response.data.error}`);
            } else {
                alert("Connection Error: Is the backend running?");
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container>
            <Typography variant="h4" sx={{ my: 2 }}>Scan MyKad</Typography>

            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                {/* Webcam View */}
                {!frontImg || !backImg ? (
                    <Webcam
                        audio={false}
                        ref={webcamRef}
                        screenshotFormat="image/jpeg"
                        width="100%"
                    />
                ) : null}

                {/* Buttons */}
                {!frontImg && (
                    <Button variant="contained" onClick={() => capture(setFrontImg)}>Capture Front</Button>
                )}
                {frontImg && !backImg && (
                    <Button variant="contained" onClick={() => capture(setBackImg)}>Capture Back</Button>
                )}

                {frontImg && backImg && (
                    <Button variant="contained" color="success" onClick={handleNext} disabled={loading}>
                        {loading ? <CircularProgress size={24} /> : "Process & Next"}
                    </Button>
                )}
            </Box>
        </Container>
    );
};

export default SignUpStep1;
