import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Button, Typography, Box } from "@mui/material";

const Home: React.FC = () => {
    const navigate = useNavigate();
    const [language, setLanguage] = useState<"BM" | "BO">("BM");

    return (
        <Box textAlign="center" mt={10}>
            <Typography variant="h3">My App Logo</Typography>

            {/* Language switch */}
            <Button
                variant="outlined"
                onClick={() => setLanguage(language === "BM" ? "BO" : "BM")}
                sx={{ mt: 2 }}
            >
                Switch to {language === "BM" ? "BO" : "BM"}
            </Button>

            {/* Login / Signup buttons */}
            <Box mt={5}>
                <Button
                    variant="contained"
                    color="primary"
                    sx={{ mr: 2 }}
                    onClick={() => navigate("/signin")}
                >
                    Login
                </Button>
                <Button
                    variant="contained"
                    color="secondary"
                    onClick={() => navigate("/signup-step1")}
                >
                    Sign Up
                </Button>
            </Box>
        </Box>
    );
};

export default Home;
