import React, { useState, useContext } from "react";
import { Box, Typography, Button } from "@mui/material";
import { useNavigate, useLocation } from "react-router-dom";
import axios from "axios";
import { LanguageContext } from "../context/LanguageContext";

const SignUpIC: React.FC = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const { language } = useContext(LanguageContext);
    const icNumber = (location.state as any)?.icNumber;

    const [frontFile, setFrontFile] = useState<File | null>(null);
    const [backFile, setBackFile] = useState<File | null>(null);

    const handleNext = async () => {
        if (!frontFile || !backFile) return alert("Please select both IC images");
        const formData = new FormData();
        formData.append("ic_number", icNumber);
        formData.append("front", frontFile);
        formData.append("back", backFile);

        try {
            await axios.post("http://localhost:8000/signup-ic", formData, { headers: { "Content-Type": "multipart/form-data" } });
            navigate("/signup-voice", { state: { icNumber } });
        } catch (err: any) {
            alert(err.response?.data?.detail || "Failed to upload IC");
        }
    };

    return (
        <Box textAlign="center" mt={5}>
            <Typography variant="h4">{language === "BM" ? "Muat Naik IC" : "Upload IC"}</Typography>
            <Box mt={2}>
                <input type="file" accept="image/*" onChange={(e) => e.target.files && setFrontFile(e.target.files[0])} />
                <input type="file" accept="image/*" onChange={(e) => e.target.files && setBackFile(e.target.files[0])} style={{ marginLeft: "10px" }} />
            </Box>
            <Box mt={3}>
                <Button variant="contained" onClick={handleNext}>{language === "BM" ? "Seterusnya" : "Next"}</Button>
            </Box>
        </Box>
    );
};

export default SignUpIC;
