import React, { useState } from 'react';
import { Button, Container, Typography, Box } from '@mui/material';
import VoiceInput from '../components/VoiceInput';
import { loginUser } from '../services/api';

const SignIn: React.FC = () => {
    const [icNumber, setIcNumber] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async () => {
        try {
            const result = await loginUser(icNumber, password);
            if (result.success) {
                alert(`Welcome back, ${result.user.fullName}!`);
            } else {
                alert("Login Failed: " + result.message);
            }
        } catch (error) {
            console.error(error);
            alert("Connection Error");
        }
    };

    return (
        <Container maxWidth="sm">
            <Box sx={{ mt: 5, display: 'flex', flexDirection: 'column', gap: 3 }}>
                <Typography variant="h4">MyKad Login</Typography>

                {/* NEW VOICE INPUT COMPONENT */}
                <VoiceInput
                    label="IC Number (Voice Enabled)"
                    value={icNumber}
                    onChange={setIcNumber}
                />

                <VoiceInput
                    label="Password"
                    value={password}
                    onChange={setPassword}
                    type="password"
                />

                <Button variant="contained" size="large" onClick={handleLogin}>
                    Login
                </Button>
            </Box>
        </Container>
    );
};

export default SignIn;
